#define product(a, b) vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.y*b.x)
#define add(a, b) vec2(a.x+b.x,a.y+b.y)


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord/iResolution.xy+vec2(-0.5,-0.5))*3.;

    float steps = sin(iTime/100.)*50.;
    float len;
    for(int x = 0; x < 20; x++){
      uv = add(product(uv, uv), vec2(.4+sin(iTime/3.)/10.,.1));
      len = uv.x*uv.x+uv.y*uv.y;
      if(len>8.){
      	steps++;
      }
    }

    steps += sin(len/20.);
    vec2 col = vec2(steps,steps);
	float val = sqrt(col.x*col.x+col.y*col.y);

    fragColor = vec4(
        sin(val/10.)/2.+0.5,
        cos(val/20.)/2.+0.5,
        cos(val/4.)/2.+0.5,
        1.0);
}
