// variant of https://shadertoy.com/view/WdtyR4

void mainImage( out vec4 O, vec2 u )
{
    O = texture(iChannel0, u / iResolution.xy);
    O = pow(O, vec4(2.2));                              // from sRGB to flat
//  u /= 2.;                                            // if you want to zoom whole pattern
    if (length(u-iMouse.xy) < 64. ) u/=8.;              // zoom pattern around mouse
    ivec2 I = ivec2(u) % 10;
    if ( min(I.x,I.y) > 0 )                             // grid pattern
        O = vec4( dot(O, vec4(.213, .715, .072, 0) ) ); // luminance

    O = pow(O, vec4(1./2.2));                           // to sRGB
}
