#define SCALE 20.0

// negative speed = moving backwards 
#define SPEED 5.0

#define DOT_SHARPNESS 15.0

#define AIR_PERSPECTIVE 3.0

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 pixel = (fragCoord -.5 * iResolution.xy) / iResolution.y;    
    vec2 trans = vec2(pixel.x / pixel.y, -1.0 / pixel.y);
    trans *= SCALE;
    float value = cos(trans.x);
    value += cos(trans.y + iTime * SPEED);
    value = (value + 2.0) / 4.0;
    value = pow(value, DOT_SHARPNESS);
    // apply air perspective
    value *= pow(abs(pixel.y), AIR_PERSPECTIVE) * 30.0;
    fragColor = vec4(vec3(value),1.0);
}
