void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float faze = cos(iTime / 1.227);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(faze + uv.xyx+vec3(0,2,4));

    float iWidth = iResolution.x * faze;
    float iHeight = iResolution.y * faze;

    if (int(fragCoord.x) % int(iWidth) == 0 && fragCoord.x > 1.0 - faze) col = vec3(1.0 - col.x,1.0-col.y,1.0-col.z);
	if (int(iResolution.x - fragCoord.x) % int(iWidth) == 0 && fragCoord.x > 1.0 - faze) col = vec3(1.0 - col.x,1.0-col.y,1.0-col.z);
	if (int(fragCoord.y) % int(iHeight) == 0 && fragCoord.y > 1.0 - faze) col = vec3(1.0 - col.x,1.0-col.y,1.0-col.z);
	if (int(iResolution.y - fragCoord.y) % int(iHeight) == 0 && fragCoord.y > 1.0 - faze) col = vec3(1.0 - col.x,1.0-col.y,1.0-col.z);

    // Output to screen
    fragColor = vec4(col,1.0);
}
