void mainImage( out vec4 O, vec2 U )
{
    O = texture(iChannel0, U/iResolution.xy);
    float l = length(O.rgb+1e-5);                  // luminance
    vec4  C = acos(O/l*2.-1.);                     // color phases

    O = .5 + .5*l* cos(iTime + C);                 // offset and recompose
  //O = l * ( .5 + .5* cos(iTime + C) );           // more logic, but get dark
}
