mat3 lerp(mat3 l, mat3 r, float var){
    var = fract(var);
    return var*l + (1.0-var)*r;

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(iChannel1, uv).rgb;

    mat3 mRGB = mat3(0.299,0.587,0.114,
                     0.299,0.587,0.114,
                     0.299,0.587,0.114);
    mat3 gen = lerp(mat3(1,0,0,0,1,0,0,0,1),mRGB,iTime/3.0);
    // Output to screen
    fragColor = vec4(col*gen,1);
}
