void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 p = (fragCoord.xy / iResolution.xy - 0.8);
float z=1./length(p.xy);
p = p * z - vec2(z+2.*mod(iTime * 1.2,33.),.1);
float a=atan(p.x,p.y)+sin(p.y+z);
fragColor=2.*abs(5.2*sin(p.x*3.+z*0.6)+sin(p.x+a*0.44)*p.xyxx*sin(vec4(a,z,z,z)))+(z-4.)*.01;
}
