const float PI = 3.14159265359;

const vec3 BASE_COLOR = vec3(0.2, 0.8, 0.5);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;

    float len = length(p);

    // atan -> -PI ~ PI
    // 0 ~ 2PI
    // 0 ~ 1
    // PIをかけるとつなぎめが分からなくなるっぽい。
    float wave = sin((atan(p.y, p.x) + PI)  / (PI * 2.0) * PI  * 100.0 );

    // 普通のcircle -> sin(len * 100.0 - iTime * 10.0);
    // そいつにwaveを足す。
    float circle = 1.0 / abs(sin(len * 20.0 - iTime * 10.0  + wave * 0.5));

    // Output to screen
    fragColor = vec4(vec3(circle) * BASE_COLOR,1.0);
}
