const vec4 background = vec4(0.18,0.18,0.18,1);
const vec4 color = vec4(1,0,0,1);
const vec2 centerUV = vec2(0.5, 0.5);
const float thicknessUV = 0.01;
const float lengthUV = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 thicknessXY = iResolution.xy * thicknessUV;
    float thickness = max(max(thicknessXY.x, thicknessXY.y), 1.);

    vec2 fragCoordUV = fragCoord/iResolution.xy;
    vec2 d = abs(centerUV - fragCoordUV) * iResolution.xy;

    vec2 lengthXY = lengthUV * iResolution.xy;
    float len = min(lengthXY.x, lengthXY.y);

    if (min(d.x, d.y) < thickness &&
        max(d.x, d.y) < len)
    {
        fragColor = color;
    }
    else
    {
        fragColor = background;
    }
}
