void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col* sawsin(uv.x * iTime+30.* sin(iTime*.1),uv.y * iTime+30.*cos(iTime*.2)),1.);
}

#define speed 5.
#define tau (8. * atan(1.))
#define Pi 22./7.
#define Pi2 Pi*2.

float sawsin(float time,float x)
{
   float t = mod((x*time)/Pi2,1.0);
   if (t>0.5)
       return -sin(x);
   if (t<=0.5)
       return 2.0*t-1.0;
}




float tri(float t,float f, float p)
{
    return abs(fract(t * f) - p)*4.0-1.0;
}

float saw(float t,float f, float p)
{
	return fract(t * f + p) * 2.0 - 1.0;
}

float sqr(float t,float f, float p)
{
    return step(fract(t * f), p)*2.0-1.0;
}


float sawmsaw(float t,float f, float p){
	return (fract(t*f)+fract(t*f*p))* 2.0 - 1.0;
}

float f(float t,float ff,float m){
    float fff = Pi*t*ff;
	return sin( fract(t)+Pi2 * sin(fff+ sin(fff+50.)) * step(m,sin(t+ fff*2.)));
}

float getNote(float t,float p,float tone){

    return fract(floor(t)/p)*tone;// + abs(sin(floor(t)/p)*tone);

}


vec2 melody(float time)
{
    float tact = step(0.0,sin(speed*4.*time));
    float cact = step(0.0,cos(speed*4.*time));
    float aftertact = smoothstep(0.93,0.198,sin(speed*time))*.5;
    float q = fract(floor(time)/speed);
	float v;
    v += f(time,100.+getNote(time,speed,400.),q)*.4;
    v += f(time,100.+getNote(time,speed*2.,400.),q)*.4;
    return vec2( v );  
}

vec2 mainSound( in int samp,float time)
{
    vec2 v = melody(time);
    v.x += sawmsaw(time,50.+getNote(time,speed*2.,400.),getNote(time,speed*2.,400.)/400.);
    v += vec2( sin(6.2831*(1340.0+fract(sin(floor(time*5.)))*200.)*fract(time))*exp(-3.0*fract(time)) );
    return  v ;  
}
