#define R iResolution.xy
void mainImage( out vec4 O, in vec2 U )
{
    O = vec4(texture(iChannel0, U/R)); // read from buffer.
}

#define R iResolution.xy
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 map(float w) {
	return palette(w, vec3(0.93, 0.32, 0.71), vec3(.65, .43, .95),
                      vec3(.38, .38, -.05)  , vec3(2.53, 2.47, -2.45));
}

void mainImage( out vec4 O, in vec2 U )
{
    if(U.x < 1.) O = vec4(map(texture(iChannel1, vec2(U.y / R.y, 0.)).x), 0); // set initial color.
    else         O = texture(iChannel0, (U - vec2(1, 0)) / R);	// grab color from pixel on the left.
}
