#define PI 3.142857
float circle(vec2 _st,float radius){
    return 1.-smoothstep(radius-0.05,radius,length(vec2(0.5) - _st));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*10.;
    uv.x *= iResolution.x/iResolution.y;
    float t = iTime*2.,x_offset = -cos(mod(t,PI)),y_offset = -cos(mod(t-PI/2.,PI));
    vec2 i = step(1.,mod(uv,2.));
    float x_f = step(0.,x_offset)*(x_offset*(1.+2.*(i.y-1.))),
        y_f = step(0.,y_offset)*(y_offset*(1.+2.*(i.x-1.))),
        color = 1.-circle(fract(uv+vec2(x_f,y_f)),0.3);
    fragColor = vec4(color,color,0.2,1.0);
}
