vec4 fbm(vec2 v) {
    float t = 0.;
    vec4 n = vec4(0.);
    float b = 1./32.;
    for (float s = 1.; s > b; s *= 0.5) {
        t += s;
    	n += texture(iChannel0, b / s * v) * s;
    }
    return n / t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.y * vec2(9./16., 1.);
    vec4 n = fbm(uv);
    vec2 m = pow(abs(vec2(n.r - n.b, n.g - n.a)) * 3., vec2(.15));
    fragColor = vec4((1. - (1. - m.x) * (1. - m.y)) * .6 + .3);
}
