#define PIXEL_SIZE 15.0
#define PI 3.141592

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor = vec4(0);

    float dx = 1.0 / iResolution.x;
    float dy = 1.0 / iResolution.y;
    uv.x = (dx * PIXEL_SIZE) * floor(uv.x / (dx * PIXEL_SIZE));
    uv.y = (dy * PIXEL_SIZE) * floor(uv.y / (dy * PIXEL_SIZE));
    // uv /= max(0.4, sin(iTime));

     for (int i = 0; i < int(PIXEL_SIZE); i++)
        for (int j = 0; j < int(PIXEL_SIZE); j++)
            fragColor += texture(iChannel1, vec2(uv.x + dx * float(i), uv.y + dy * float(j)));

    fragColor /= pow(PIXEL_SIZE, 2.0);
    float gray = (fragColor.r + fragColor.g + fragColor.b) / 3.;


    float x,y;
    x=fract((fragCoord.x * uv.x));
    y=fract(fragCoord.y*uv.y);

    if(cos(PI * fragCoord.x / PIXEL_SIZE * 2.) < mod(gray * 10., 6.) ||
      cos(PI * fragCoord.y / PIXEL_SIZE * 2.) <  mod(gray * 10., 6.)) {
         fragColor = vec4(0., 0., 0., 1.0);
    }
    else {

        fragColor = vec4(vec3(1.), 1.0);
    }
    fragColor = vec4(vec3(gray), 1.0);

}
