void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//Get camera feed
    vec4 color = texture(iChannel0,fragCoord/iResolution.xy);

    //convert to greyscale
    float bw = (min(color.r, min(color.g, color.b)) + max(color.r, max(color.g, color.b))) * 0.5;

    //create banding
    bw = mod(bw, 1.0/(sin(iTime/5.)*10.)) * (sin(iTime/5.)*10.);

    vec4 greyscale = vec4(0., bw, 0., 1.0);



    // Output to screen
    fragColor = greyscale;
}
