void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //when working is mobile opengl 2.x 
    //uv.y = 1.0 - uv.y;

    float red = mod(uv.x,0.125) * 8.0;

    float green = mod(uv.y,0.125) * 8.0;

    float xindex = floor(uv.x / 0.125);

    float yindex = floor(uv.y / 0.125);

    float blue = (yindex * 8.0 + xindex) * 4.0 / 256.0;

    vec3 col = vec3(red,green,blue);
    //this is animation code fun
    //vec3 col = vec3(red,green,abs(sin(iTime * blue)));

    fragColor = vec4(col,1.0);

}
