#define ANIMATION_TIMESCALE 0.5

//https://www.shadertoy.com/view/4dc3zr

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * fragCoord / iResolution.xy - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(0.0);
    float t = iTime * ANIMATION_TIMESCALE;    
    float udCurve = uv.y - clamp(uv.x, -0.1, 0.1);
    float aaf = fwidth(udCurve);
    float PXSIZE = 1./iResolution.x;
    aaf = 5.0 * PXSIZE;
    udCurve = abs(udCurve);
    float x = uv.x - 3.0 * sin(t) * sign(cos(t));
    x *= 2.0;
    float mask = smoothstep(-1.0, -0.6, x) - smoothstep(0.6, 1.0, x);  
    col += mix(vec3(1.0) * mask, col, smoothstep(0., aaf, udCurve));	
    fragColor = vec4(col, 1.0);
}
