void mainImage(out vec4 fragColor,vec2 fragCoord){
  vec3 r=normalize(vec3(fragCoord.xy/500.-vec2(.5),1.)),p=vec3(-.44,.11,-10.+iTime/2.);
  for(float i=.0;i<99.;i++){
    vec4 o=vec4(p,1),q=o;
    for(float i=0.;i<9.;i++){
      o.xyz=clamp(o.xyz,-1.,1.)*2.-o.xyz;
      o=o*clamp(max(.25/dot(o.xyz,o.xyz),.25),0.,1.)*vec4(11.2)+q;
    }
    float d=(length(o.xyz)-1.)/o.w-5e-4;
    if(d<5e-4){break;}
    p+=r*d;
    fragColor.rgb=vec3(1.-i/50.-normalize(o.xyz)*.25);
  }
}
