#define RADIUS 0.7
#define THIKNESS 0.009


float sphereSDF(vec2 p){
	return length(p)-RADIUS;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;


    float sdf = sphereSDF(uv);
    float sm = smoothstep(0.0,THIKNESS,sdf);

    float sm2 =sm*(1.0-sm);
    sm2*=1000.0;

   //outside
    vec3 outside =vec3(0.95,0.95,0.87)*sin(iTime*0.3);


   // inside   
    vec2 x=fragCoord.xy;
	vec3 inside=vec3(max((fract(dot(sin(x),x))-.950)*10.*abs(tan(iTime*0.4)),.0));


    /// edge
    vec3 edge=vec3(0.05,abs(sin(iTime)*0.05),sin(iTime*4.0)*0.05);


    //blend it all
    vec3 mixedcol = mix(inside,outside,sm);
    vec3 col=mix(mixedcol,edge,sm2);




   fragColor = vec4(col,1);

}
