//to get a higher float precision
precision highp float;
const float PI = 3.1415926535897932384626433832795;


float Heart( in vec2 fragCoord,float heartId){

    vec2 center0 = vec2(iResolution.x/2.,iResolution.y/2.);
    float X = (fragCoord.x- center0.x )*(heartId*(-1.)*cos(iTime));
    float Y = (fragCoord.y- center0.y) *abs(-cos(iTime));

    float a =(X*X);
    float b =  ( pow( X,(2.7/3.)));
    float c = (Y - b);

    return a+c*c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( fragCoord-.5*iResolution.xy )/iResolution.y;

    // Time varying pixel color
     vec3 col = vec3(0.,0.,0.);

    if(Heart(fragCoord,1.) <=10000.){
        col = vec3(1.,0.,0.5);
     }

    if(Heart(fragCoord,-1.) <=10000.){
        col = vec3(1.,0.,0.5);
     }
    // Output to screen
    fragColor = vec4(col,1.0);
}
