// Use mouse to add powder. Q to remove powder
vec2 R;
void mainImage( out vec4 f, in vec2 u ){
    R = iResolution.xy;

    f = vec4(vec3(texelFetch(iChannel0, ivec2(u), 0).x), 1.0);
}

#define A(p) texelFetch(iChannel0, ivec2(p), 0)
#define KEY(v,m) texelFetch(iChannel1, ivec2(v, m), 0).x
mat2x4 N(vec2 u){
    return mat2x4(
        vec4(
            A(u+vec2(0., 1.)).x,
        	A(u+vec2(0., -1.)).x, 
        	A(u+vec2(1., 0.)).x, 
        	A(u+vec2(-1., 0.)).x
        ),
        vec4(
            A(u+vec2(-1., 1.)).x,
        	A(u+vec2(-1., -1.)).x,
        	A(u+vec2(1., 1.)).x,
        	A(u+vec2(1., -1.)).x
        )
    );
}
void mainImage( out vec4 f, in vec2 u ){
    vec4 bA = A(u);
    mat2x4 n = N(u);

    if(n[0].x == 1. && bA.x == 0.)bA.x = 1.;
    else if(bA.x == 1. && n[0].y == 0.)bA.x = 0.;

    if(n[0].x == 0.){
    	if(n[1].x + n[0].w + n[1].y == 3.) bA.x = 1.;
    	if(n[1].z + n[0].z + n[1].w == 3.) bA.x = 1.;
    }

    if(length(iMouse.xy-u) < 30. && iMouse.z > 1.) bA.x = 1.-KEY(81., 0.);
    if(iFrame < 4) bA = vec4(0);

    f = bA;
}
