vec2 cossin(float f) { return vec2(cos(f), sin(f)); }
vec2 translate(vec2 uv, vec2 offset) { return uv - offset; }
float circle(vec2 uv, vec2 center, float r) { return length(uv - center) - r; }
float outline(float dist, float r) { return abs(dist)-r; }
float subtract(float d1, float d2) { return max(d1, -d2); }
float add(float d1,float d2) { return min(d1, d2); }
float and(float d1,float d2) { return max(d1, d2); }

vec2 rotate(vec2 uv, float a) {
    vec2 cs = cossin(a);
    return mat2x2(cs.x, -cs.y, cs.y, cs.x) * uv;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    //


    uv *= 3.0;
    uv = fract(uv*.5+.5)*2.0-1.0;
    uv.x = -abs(uv.x);
    uv.x += sin(uv.y*10.0-iTime)*.05;


    float head = outline(circle(uv, vec2(0,0), 1.0), .01);


    vec2 eyeOffset = vec2(-.4,.4);
    float eyeL = circle(uv, eyeOffset, .10);
    float pupilL = circle(uv, eyeOffset + .05 * cossin(iTime*3.0), .05);
    eyeL = subtract(eyeL, pupilL);




    uv = translate(uv, vec2(0, -.25));
    uv = rotate(uv, iTime);
    float mouth = outline(
        and(circle(uv, vec2(0, -.05), .30),
        	circle(uv, vec2(0, .05), .30))
        , .01);



    float dist = head;
    dist = add(dist, eyeL);
    dist = add(dist, mouth);

    fragColor = vec4(1.0-dist*50.0);
}
