#define GREYSCALE 0
#define LUMINANCE_COSINE_PALETTE 1
#define CONTRAST 0
#define SMOOTHSTEP 0
#define BRANNAN 0
#define SUNSET 0
#define BLOOM 0
#define POSTERIZE 0
#define POSTERIZE_RGB 0
#define COLOR_OFFSET 0
#define GAMMA 0

const float CONTRAST_FACTOR = 128.0; // -255 to 255 is valid range
const float SMOOTHSTEP_POW = 1.0;
const float POSTERIZE_AMOUNT = 5.0;

const float POSTERIZE_AMOUNT_R = 3.0;
const float POSTERIZE_AMOUNT_G = 7.0;
const float POSTERIZE_AMOUNT_B = 2.0;

// Cosine palette variables
const vec3 a = vec3(0.5, 0.5, 0.5);
const vec3 b = vec3(0.5, 0.5, 0.5);
const vec3 c = vec3(1.0, 1.0, 1.0);
const vec3 d = vec3(0.0, 0.33, 0.67);

vec3 cosinePalette(float t) {
    return a + b * cos(6.2831 * (c * t + d));
}

vec3 greyscale(vec3 c) {
    return vec3(0.21 * c.r + 0.72 * c.g + 0.07 * c.b);
}

const vec3 sunset0 = vec3(255, 229, 119) / 255.0;
const vec3 sunset1 = vec3(254, 192, 81) / 255.0;
const vec3 sunset2 = vec3(255, 137, 103) / 255.0;
const vec3 sunset3 = vec3(253, 96, 81) / 255.0;
const vec3 sunset4 = vec3(57, 32, 51) / 255.0;
vec3 sunsetPalette(float t);

// Formula from https://www.dfstudios.co.uk/articles/programming/image-programming-algorithms/image-processing-algorithms-part-5-contrast-adjustment/
vec3 contrast(vec3 rgb, float c) {
    float f = 259.0 * (c + 255.0) / (255.0 * (259.0 - c));
    return clamp(f * (rgb - vec3(0.5)) + vec3(0.5), 0.0, 1.0);
}

// Map f(x) = 0 to a, f(x) = 1 to b, and LERP between them, when f(x) is linear
float linearRemapY(float a, float b, float t) {
    return (t * (b - a)) + a;
}

// Clamp t values lower than a to 0, higher than b to 1, and LERP between the rest
float linearRemapX(float a, float b, float t) {
    return clamp((t - a) * (1.0 / (b - a)), 0.0, 1.0);
}

vec3 linearRemapY(float a, float b, vec3 t) {
    return (t * (b - a)) + a;
}

// Clamp t values lower than a to 0, higher than b to 1, and LERP between the rest
vec3 linearRemapX(float a, float b, vec3 t) {
    return clamp((t - a) * (1.0 / (b - a)), 0.0, 1.0);
}

vec3 vignette(vec2 uv, float inner, vec3 color, vec3 vignetteColor) {
	float vignette = smoothstep(inner, 0.0, length((2.0 * uv) - vec2(1.0)));
    return mix(color * vignetteColor, color, vignette);
}

vec3 smoothstepPow(vec3 c, float p) {
    return pow(smoothstep(0.0, 1.0, c), vec3(p));
}

vec3 posterize(vec3 c) {
    vec3 c255 = vec3(255.0 * c);
    return vec3(c255 - vec3(mod(c255, vec3(pow(2.0, POSTERIZE_AMOUNT))))) / 255.0;
}

vec3 posterizeRGB(vec3 c) {
    vec3 c255 = vec3(255.0 * c);
    c.r = (c255.r - mod(c255.r, pow(2.0, POSTERIZE_AMOUNT_R))) / 255.0;
    c.g = (c255.g - mod(c255.g, pow(2.0, POSTERIZE_AMOUNT_G))) / 255.0;
    c.b = (c255.b - mod(c255.b, pow(2.0, POSTERIZE_AMOUNT_B))) / 255.0;
    return c;
}

float random1(float t) {
    return 2.0 * fract(sin(t * 489.12342) * 348921.32457) - 1.0;
}

vec3 remapColor(vec3 c, float offset, float seed) {
    float ro = random1(seed) * offset;
    float go = random1(seed + 31242.134) * offset;
    float bo = random1(seed + 73576.347) * offset;

    return clamp(vec3(c.r + ro, c.g + go, c.b + bo), 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = texture(iChannel0, uv).rgb;
    float split = iMouse.x;
    if(iMouse.x < 0.01) split = iResolution.x / 2.0;

    if(fragCoord.x > split) {
        #if GREYSCALE
        color = greyscale(color);
        #elif LUMINANCE_COSINE_PALETTE
        color = greyscale(color);
        color = cosinePalette(color.x + iTime * 0.25);
        #elif CONTRAST
        color = contrast(color, CONTRAST_FACTOR);
        #elif SMOOTHSTEP
        color = smoothstepPow(color, SMOOTHSTEP_POW);
        #elif BRANNAN
        color = contrast(color, 100.0);
        color = mix(color, vec3(243.0, 239.0, 167.0) / 255.0 * color, 0.5);
        color = linearRemapY(42.0 / 255.0, 1.0, color);
        color = vignette(uv, 1.85, color, vec3(0.5, 0.25, 0.0));
        #elif SUNSET
        vec3 c = greyscale(color);
        c = mod(5.0 *(vec3(iTime * 0.2) + c), 5.0) * 0.2;
        color = sunsetPalette(c.r);
        #elif BLOOM
        color = min(vec3(1.0), color + texture(iChannel1, uv).rgb * 5.0);
        #elif POSTERIZE
        color = posterize(color);
        #elif POSTERIZE_RGB
        color = posterizeRGB(color);
        #elif COLOR_OFFSET
        color = mix(remapColor(color, 0.2, floor(iTime)), remapColor(color, 0.2, floor(iTime) + 1.0), fract(iTime));
        #elif GAMMA
        color = pow(color, vec3(2.2));
        #endif
    }

    fragColor = vec4(color, 1.0);
}

vec3 sunsetPalette(float t) {
    if(t < 0.2) {
        return mix(sunset0, sunset1, t / 0.2);
    }
    else if(t < 0.4) {
        return mix(sunset1, sunset2, (t - 0.2) / 0.2);
    }
    else if(t < 0.6) {
        return mix(sunset2, sunset3, (t - 0.4) / 0.2);
    }
    else if(t < 0.8) {
        return mix(sunset3, sunset4, (t - 0.6) / 0.2);
    }
    else if(t < 1.0) {
        return mix(sunset4, sunset0, (t - 0.8) / 0.2);
    }
}

const int RAY_STEPS = 256;
const float HALF_PI = 3.14159 * 0.5;

const vec3 MAIN_LIGHT_DIR = normalize(vec3(1.0, 0.2, -0.4));

float random1(vec2 p) {
  return fract(sin(dot(p, vec2(456.789, 20487145.123))) * 842478.5453);
}

float random1( vec3 p ) {
  return fract(sin(dot(p, vec3(127.1, 311.7, 191.999))) * 43758.5453);
}

vec2 random2(vec2 p) {
  return fract(sin(vec2(dot(p, vec2(127.1, 311.7)), dot(p, vec2(269.5, 183.3)))) * 43758.5453);
}

float mySmootherStep(float a, float b, float t) {
  t = t*t*t*(t*(t*6.0 - 15.0) + 10.0);
  return mix(a, b, t);
}

float interpNoise3D1(vec3 p) {
  vec3 pFract = fract(p);
  float llb = random1(floor(p));
  float lrb = random1(floor(p) + vec3(1.0,0.0,0.0));
  float ulb = random1(floor(p) + vec3(0.0,1.0,0.0));
  float urb = random1(floor(p) + vec3(1.0,1.0,0.0));

  float llf = random1(floor(p) + vec3(0.0,0.0,1.0));
  float lrf = random1(floor(p) + vec3(1.0,0.0,1.0));
  float ulf = random1(floor(p) + vec3(0.0,1.0,1.0));
  float urf = random1(floor(p) + vec3(1.0,1.0,1.0));

  float lerpXLB = mySmootherStep(llb, lrb, pFract.x);
  float lerpXHB = mySmootherStep(ulb, urb, pFract.x);
  float lerpXLF = mySmootherStep(llf, lrf, pFract.x);
  float lerpXHF = mySmootherStep(ulf, urf, pFract.x);

  float lerpYB = mySmootherStep(lerpXLB, lerpXHB, pFract.y);
  float lerpYF = mySmootherStep(lerpXLF, lerpXHF, pFract.y);

  return mySmootherStep(lerpYB, lerpYF, pFract.z);
}


float fbm(vec3 p, float octaves) {
  float amp = 0.5;
  float freq = 8.0;
  float sum = 0.0;
  float maxSum = 0.0;
  for(float i = 0.0; i < 10.0; ++i) {
    if(i == octaves)
    break;
    maxSum += amp;
    sum += interpNoise3D1(p * freq) * amp;
    amp *= 0.5;
    freq *= 2.0;
  }
  return sum / maxSum;
}

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

vec3 rotateX(vec3 p, float amt) {
    return vec3(p.x, cos(amt) * p.y - sin(amt) * p.z, sin(p.y) + cos(p.z));
}

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

void raycast(vec2 uv, out vec3 dir, out vec3 eye, out vec3 ref) {
    eye = rotateY(vec3(0.0, 0.0, 15.0), sin(30.0) * 3.14159 * 0.5);
    //eye = vec3(0.0, 5.0, 5.0);
    ref = vec3(0.0, 0.0, 0.0);

    float len = tan(3.14159 * 0.125) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * iResolution.x / iResolution.y;
    vec3 p = ref + uv.x * H + uv.y * V;
    dir = normalize(p - eye);
}

float sphere(vec3 p, float r, vec3 c)
{
    return distance(p, c) - r;
}

float torus(vec3 p, vec2 t)
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q) - t.y;
}

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}

// Version that just returns the t value, for surface normal computation
float sceneMap3D(vec3 pos)
{
    float t = sphere(pos, 2.0, vec3(0.0, 0.0, 0.0));
    t = min(t, sphere(pos, 2.0, vec3(12.0, 0.0, -6.0)));
    t = min(t, sphere(pos, 2.0, vec3(-8.0, 0.0, 4.0)));
    t = min(t, box(pos + vec3(0.0, 3.0, 0.0), vec3(50.0, 1.0, 50.0)));
    return t;
}

#define CENTER_SPHERE 0
#define LEFT_SPHERE 1
#define RIGHT_SPHERE 2
#define FLOOR 3
// Version that returns t value and the ID of the object hit
void sceneMap3D(vec3 pos, out float t, out int obj)
{
    t = sphere(pos, 2.0, vec3(0.0, 0.0, 0.0));
    float t2;
    obj = CENTER_SPHERE; // 0 is center sphere
    if((t2 = sphere(pos, 2.0, vec3(12.0, 0.0, -6.0))) < t) {
        t = t2;
        obj = LEFT_SPHERE; // 1 is left sphere
    }
    if((t2 = sphere(pos, 2.0, vec3(-8.0, 0.0, 4.0))) < t) {
        t = t2;
        obj = RIGHT_SPHERE; // 2 is right sphere
    }
    if((t2 = box(pos + vec3(0.0, 3.0, 0.0), vec3(50.0, 1.0, 50.0))) < t) {
        t = t2;
        obj = FLOOR; // 3 is floor
    }
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
    	float m;
        sceneMap3D(pos, m, hitObj);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

vec3 skyColor(vec3 dir) {
    float t = smoothstep(0.0, 1.0, dir.y);
    t = clamp(0.0, 1.0, t + fbm(dir / 8.0, 4.0) * 0.1);
    t = fbm(dir / 8.0, 6.0);
    t = smoothstep(0.0, 1.0, t);
    vec3 dawnGradient = mix(vec3(253.0, 96.0, 81.0) / 255.0, vec3(255.0, 229.0, 119.0) / 255.0, t);
    vec3 duskGradient = mix(vec3(48.0, 24.0, 96.0) / 255.0, vec3(144.0, 96.0, 144.0) / 255.0, t);
    t = max(0.0, dot(dir, MAIN_LIGHT_DIR));
    return mix(duskGradient, dawnGradient, t);
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 d, vec3 n) {
    switch(hitObj) {
        case CENTER_SPHERE:
        // Center sphere
        return vec3(1.0, 0.67, 0.67);
        break;
        case LEFT_SPHERE:
        // Back sphere
        return vec3(0.67, 1.0, 0.67);
        break;
        case RIGHT_SPHERE:
        // Front sphere
        return vec3(0.67, 0.67, 1.0);
        break;
        case FLOOR:
        // Floor
        float t = floor(mod((sin(p.x) + sin(p.z)) * 0.5, 2.0));
        return mix(vec3(0.7, 0.4, 0.2), vec3(1.0), t);
        break;
        case -1:
        // Background
        break;
    }
    return vec3(1.0);
}

Intersection sdf3D(vec3 dir, vec3 eye)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj);

    if(t == -1.0) {
        return Intersection(t, skyColor(dir), vec3(eye + 1000.0 * dir), -1);
    }

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect);

    vec3 material = computeMaterial(hitObj, isect, dir, nor);

    float fresnel = 1.0 - max(0.0, dot(normalize(eye - isect), nor));
    fresnel = 0.25 + 0.75 * fresnel;
    vec3 sdfColor = mix(material, skyColor(reflect(dir, nor)) * material, fresnel);

    return Intersection(t, sdfColor, isect, hitObj);
}

float luminance(vec3 c) {
    return 0.21 * c.r + 0.72 * c.g + 0.07 * c.b;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // [-1, 1]
    vec2 uv2 = 2.0 * uv - vec2(1.0);

    // Stratified sampled 4x4 anti-aliasing
    Intersection aaIsects[16];
    vec3 dir, eye, ref;
    int idx = 0;
    for(float i = 0.0; i < 1.0; i += 0.25) {
        for(float j = 0.0; j < 1.0; j += 0.25) {
            raycast(uv2 + (vec2(i, j) + random2(vec2(i, j))) / iResolution.xy, dir, eye, ref);
            aaIsects[idx++] = sdf3D(dir, eye);
        }
    }
    vec3 avgColor = vec3(0.0);
    for(int i = 0; i < 16; ++i) {
        avgColor += aaIsects[i].color;
    }
    avgColor *= 0.0625;
    fragColor = vec4(avgColor, luminance(avgColor));
}

// from https://www.shadertoy.com/view/XdfGDH
float normpdf(in float x, in float sigma)
{
	return 0.39894*exp(-0.5*x*x/(sigma*sigma))/sigma;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = texture(iChannel0, uv).rgb;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    // from https://www.shadertoy.com/view/XdfGDH
    const int mSize = 11;
    const int kSize = (mSize-1)/2;
    float kernel[mSize];
    vec3 final_color = vec3(0.0);

    //create the 1-D kernel
    float sigma = 20.0;
    float Z = 0.0;
    for (int j = 0; j <= kSize; ++j) {
        kernel[kSize+j] = kernel[kSize-j] = normpdf(float(j), sigma);
    }

    //get the normalization factor (as the gaussian has been clamped)
    for (int j = 0; j < mSize; ++j) {
        Z += kernel[j];
    }

    //read out the texels
    for (int i=-kSize; i <= kSize; ++i) {
        for (int j=-kSize; j <= kSize; ++j) {
            vec4 bufACol = texture(iChannel0, (fragCoord.xy+vec2(float(i),float(j))) / iResolution.xy);
            final_color += kernel[kSize+j]*kernel[kSize+i]*bufACol.rgb * step(0.6, bufACol.a);

        }
    }
    fragColor = vec4(final_color, 1.0);
}
