void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // dear god I wish I knew why this was doing anything cool
    fragColor = 
        texture(iChannel0, uv*1.0) 
        / texture(iChannel0, uv*2.0) 
        * texture(iChannel0, uv*3.0) 
        / texture(iChannel0, uv*0.5);
}
