float signmod(float x, float m) {
	return mod(x, m) - step(x, 0.) * m;
}

vec3 signmod(vec3 v, float m) {
	return vec3(signmod(v.x, m), signmod(v.y, m), signmod(v.z, m));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos2 = 2. * fragCoord / iResolution.xy - 1.;
    pos2.x *= iResolution.x / iResolution.y;
    vec3 pos = vec3(pos2, 1.);
    vec3 dir = normalize(pos);

    const int Steps = 6;
    int i;
    for (i = 0; i < Steps; i += 1) {
    	float d = length(pos - vec3(0., 0., 5.)) - 1.;
        pos = signmod(pos + dir * d, sin(iTime / 2.) * 10.);
    }

    fragColor = vec4(length(pos / 10.), sin(pos.z + iTime) / 2. + .5, pos.z, 1.);
}
