void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 q = uv - vec2(tan(iTime*1.4)*.01,tan(iTime*1.2)*.01);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*(q.xyx+vec3(0,2,4));

    float n = sin(iTime*2.1)*.1+1.;
    float m = sin(iTime*.8)*.1+1.;

    col.x = smoothstep(sin(col.x*100.)*1.1,(sin(iTime*1.)*1.1),n);
    col.y = smoothstep(cos(col.y*100.)*1.2,(cos(iTime*1.)*1.),m);

    // Output to screen    

	fragColor = vec4(col,1.0);    

}
