// Distance from p to ab
float sdLine(in vec2 p,in vec2 a,in vec2 b)
{
    vec2 origin = a;    
    vec2 p2 = p-origin;
    vec2 b2 = b-origin;

	float lineRatio = dot(p2, b2)/dot(b2, b2);
    lineRatio = clamp(lineRatio, 0., 1.);

    vec2 pointOnLine = b2*lineRatio;
    return length(p2 - pointOnLine);
}

// Randomness from https://www.shadertoy.com/view/tsf3Dn
int xorshift(in int value) {
    // Xorshift*32
    // Based on George Marsaglia's work: http://www.jstatsoft.org/v08/i14/paper
    value ^= value << 13;
    value ^= value >> 17;
    value ^= value << 5;
    return value;
}

int seed = 0;

int nextInt() {
    seed = xorshift(seed);
    return seed;
}

// Main

const vec2 lineStart = vec2(0.5, 1.0);
const float lineEndX = 0.5;
const float height = 0.9;
const int numberOfSegments = 5;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // setup randomness for frame
    seed = int(floor(iTime * 10.0));

    // Scaled pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 linePoint = lineStart;

    float brightness = 0.0;

    for (int i = 0; i < numberOfSegments; i++) {
      // 0, 1, 2, or 3
      int stage = nextInt() % 4;
      vec2 a = linePoint;
      vec2 b = vec2(lineEndX, a.y - (height / float(numberOfSegments)));
      if (i != numberOfSegments - 1) {
        b.x -= 0.1;
        b.x += 0.05 * float(stage);
      }
      float dist = sdLine(uv, a, b);
      brightness += 1. - step(0.01, dist);
      linePoint = b;
    }

    vec3 colour = vec3(brightness);

    // Output to screen
    fragColor = vec4(colour,1.0);
}
