void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float s=0.34;
    // Time varying pixel color

    vec3 col = floor((pow(1.0-pow(uv.x-0.5,2.0),12.0)*pow(1.0-pow(uv.y-0.5,2.0),4.0))+s)
        *vec3(1,0,0)+
        (1.0-floor((pow(1.0-pow(uv.x-0.5,2.0),12.0)*pow(1.0-pow(uv.y-0.5,2.0),4.0))+s))
		*vec3(1,1,1);

      col *= (pow(1.0-pow(uv.x-0.5,2.0),2.0)*pow(1.0-pow(uv.y-0.5,2.0),2.0))*vec3(1,1,1);
    // Output to screen
    fragColor = vec4(col,1.0);
}
