// reference from https://www.shadertoy.com/view/XssSzN

#define A float (5.0/4.0)
#define B float (4.0/4.0)

vec3 metaballs(vec2 uv)
{
    float v = 0.5;
    float P = 1.5 ; //3. + 3.*(1.+sin(0.008*iTime));
    for (int i; i<35; i++) {
        vec2 c = vec2(1.65*sin(A*(.02*iTime-float(i)*P) +3.141592/4.), sin(B*(.02*iTime-float(i)*P)));
        float tempV = 1.0-smoothstep(0.007, 0.25, length(uv-0.5*c));
        v += tempV;
    }

    vec3 c = mix(vec3(0.2), vec3(.5*(1.+sin(iTime)*uv.y),uv.x+uv.y,1.0-uv.x), smoothstep(.06,.91,v*.5));
    return c;
}

float circle(vec2 p , vec2 c, float r)
{
    vec2 dist = (p-c)*.2;
    float a = smoothstep(r, r+0.01, length(dist));
    return a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2. *  fragCoord.xy / iResolution.xy - 1.);
	uv.y *= iResolution.y/iResolution.x;
    vec3 col = vec3(0);
    vec3 line = vec3(0);
   for (int i = 0; i<500; i++) {
       float P =  .03 ; //+ (1.+sin(0.015*iTime));
       vec2 pos = vec2(1.65*sin(A*(.2*iTime-float(i)*P) +3.141592/4.), sin(B*(.2*iTime-float(i)*P)));
   	line += vec3(.08)-vec3(circle(uv, 0.55*pos, 0.008));
   }
    line = vec3(.1*(2.+sin(3.*line.x) + sin(-uv.x*2.75)),line.x*.5*(1.+sin(uv.y*3.)),.25*(1.+sin(uv.y*3.5*cos(iTime))));

    col += metaballs(uv) + line;

    fragColor = vec4(clamp(col, 0., 1.), 1);
}
