void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{

  vec2 st = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

  vec3 b = vec3(greaterThanEqual(vec3(0.8), vec3(max(abs(st.x), (abs(st.y) * 1.777))))); 
  vec3 n0 = vec3(st.x * 3. * abs(st.x), -st.y + .01, .011);
  vec3 n1 = vec3(-b.x - .25, sin(b.y * 2.) - .2, .01);

  float col = max(0.01, dot(reflect(normalize(b), normalize(cross(n0, n1))), vec3(-1., .7, -1.4)));

  (b.x == 1. && b.y == 1.) ?
  fragColor.rgb = ((vec3(0.91, 0.501, 0.1) * col) + 0.04)
      : fragColor.rgb = vec3(1.0);

  fragColor.a = 1.;
}
