void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float sum  = 0.0, 
          seed = 0.0;
    float time = sin(iTime * .5) * 2.0;

    for (int i = 0; i < 100; i ++)
    {
        seed =  mod(seed * 106.0 + 1283.0, 6075.0);
        sum  += (iResolution.x + iResolution.y) / 600.0 / distance(fragCoord.xy, vec2(
            iResolution.x / 2.0 + mod(seed, iResolution.x) * cos(-time - seed) * time,
            iResolution.y / 2.0 + mod(seed, iResolution.y) * sin(seed  + time) * time
        ));
    }
    fragColor = mix(
        vec4(fragCoord.xy / iResolution.xy, 0.5 + 0.5 * sin(time), 1.0), 
        vec4(sum, 0.0, sum, sum), 0.6
    );
}
