const int nRect = 3;
const float vel = .1;
const int rep = 1;
float divs[nRect*rep];

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord/iResolution.xy;
  vec3 color[nRect];
  color[0] = vec3(1, 0, 0);
  color[1] = vec3(0, 1, 0);
  color[2] = vec3(0, 0, 1);

  int lDiv = 0;
  int rDiv = 0;
  for (int i = 0; i < nRect*rep; i++) {
   	divs[i] = (float(i)/float(nRect*rep) + iTime*vel) - floor(float(i)/float(nRect*rep) + iTime*vel);
    divs[rDiv] < divs[i] ? rDiv = i : rDiv = rDiv;
    divs[lDiv] > divs[i] ? lDiv = i : lDiv = lDiv;
  }

  for (int i = 0; i < nRect*rep; i++) {
    if (i == rDiv) {
      float div1 = divs[rDiv];
      float div2 = divs[lDiv];
        if ((uv.x > div1) || (uv.x < div2))
        	fragColor = vec4(color[i % nRect], 1);
    } else {
      	float div1 = divs[i];
      	float div2 = i+1 == nRect*rep ? divs[0] : divs[i+1];
        if ((uv.x > div1) && (uv.x < div2))
      		fragColor = vec4(color[i % nRect], 1);
    }
  }
}
