int iter = 256;
vec2 view_dim = vec2(2.0,1.125);
vec2 view_pos = vec2(-0.9121,0.264);

void mainImage(out vec4 fragColor,in vec2 fragCoord)
	{
    vec2 uv = fragCoord/iResolution.xy;

    float zoom = pow(10.0,-0.3+(iMouse.y/iResolution.y)*6.0);
    float ix = view_pos.x + ((iMouse.x/iResolution.x)*0.5)/zoom - ((view_dim.x/zoom)/2.0) + ((view_dim.x/zoom)*uv.x);
    float iy = view_pos.y - ((view_dim.y/zoom)/2.0) + ((view_dim.y/zoom)*uv.y);

    float t = 0.0;
    float x = 0.0;
    float y = 0.0;

    int i = 0; // iterate through 'z[n+1] = sqr(z[n]) + c'
    while((x*x + y*y < 4.0) && (i++ < iter))
    	{
        t = ix + (x*x - y*y);
        y = iy + (2.0 * x*y);
        x = t;
    	}

    // output
    vec3 v = (i < iter) ? vec3(0.1 + (float(i)/float(iter))*0.9) : vec3(0.0);
    fragColor = vec4(v,1.0);
	}
