// Animated Shadow
// my step by step tutorial 4
// by Arthur Stammet, 03.2019

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set center to the middle of the screen
    vec2 p = (fragCoord-.5*iResolution.xy)/iResolution.y; 

    // change values here --------------------------------------------------------------

    // Background Color-Cycling RGB-Values
    float SpeedR = iTime * 0.7;
    float SpeedG = iTime * 0.8;
    float SpeedB = iTime * 0.9;

    // Parameters for the Lissajous Curve ( Ellipse if SpeedX = SpeedY )
    float SpeedX = iTime * 0.62;
    float SpeedY = iTime * 0.75;

    // ---------------------------------------------------------------------------------

	float red = 0.5 + sin(SpeedR*0.5); 		// 0.0 <> 1.0
	float green = 0.5 + sin(SpeedG*0.5);	// 0.0 <> 1.0
	float blue = 0.5 + sin(SpeedB*0.5); 	// 0.0 <> 1.0

    // creating a Lissajous Curve
    float CenterX = sin(SpeedX)*0.4;
	float CenterY = cos(SpeedX)*0.2;

    vec3 col = vec3( red, green, blue ); // colorized screen (animated)	
	col *= length(p - vec2(CenterX,CenterY)); // moving shadow (animated)

    fragColor = vec4(col, 1.0);
}
