vec3 GTAOMultiBounce(float visibility, vec3 albedo)
{
 	vec3 a =  2.0404 * albedo - 0.3324;   
    vec3 b = -4.7951 * albedo + 0.6417;
    vec3 c =  2.7552 * albedo + 0.6903;

    vec3 x = vec3(visibility);
    return max(x, ((x * a + b) * x + c) * x);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 albedo = vec3(0);

    if(uv.y < 0.1)
    {
     	albedo = vec3(1.0, 0.0, 0.0);
    }
    else if(uv.y < 0.2)
    {
        albedo = vec3(0.0, 1.0, 0.0);
    }
    else if(uv.y < 0.3)
    {
        albedo = vec3(0.0, 0.0, 1.0);
    }
    else if(uv.y < 0.4)
    {
     	albedo = vec3(1.0, 0.0, 1.0);
    }
    else if(uv.y < 0.5)
    {
        albedo = vec3(1.0, 1.0, 0.0);
    }
    else if(uv.y < 0.6)
    {
        albedo = vec3(0.0, 1.0, 1.0);
    }
    else if(uv.y < 0.7)
    {
     	albedo = vec3(0.5, 0.0, 1.0);
    }
    else if(uv.y < 0.8)
    {
        albedo = vec3(1.0, 0.5, 0.0);
    }
    else if(uv.y < 0.9)
    {
        albedo = vec3(0.0, 0.5, 1.0);
    }
    else
    {
        albedo = vec3(0.5, 0.5, 1.0);
    }

    vec3 bounceColor = GTAOMultiBounce(uv.x, albedo);

    fragColor = vec4(bounceColor, 0);
}
