vec3 red = vec3(1.,0.,0.);
vec3 blue = vec3(0.,0.,1.);
vec3 green = vec3(0,1.,0.);
vec3 yellow = vec3(1.,1.,0.);
vec3 black = vec3(0.,0.,0.);
vec3 gray = vec3(.5,.5,.3);

float virus(vec2 uv,float posX,float posY,float midSize,float outerSize)
{
    float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    vec2 shapePos= vec2(posX*ratio,posY);
    float dist = distance(shapePos,uv)*2.;

    uv -=shapePos;
    float angle= atan(uv.y,uv.x);
    float radius = cos(2.*angle);
    radius = abs(cos(angle*12.+iTime)*sin(angle*3.+iTime))*outerSize+midSize;

    float value = 1.-step(radius,dist);


    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;


    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float value = virus(uv,.5,.5,.5,.2);
	vec3 val = mix(col,col*2.,value);


    // Output to screen
    fragColor = vec4(val,uv);
}
