void mainImage(out vec4 fragColor,in vec2 fragCoord)
	{
    float off_y = -iTime/3.0;

    // sample
    vec2 uv = fragCoord/iResolution.xy;
    float v = texture(iChannel0,vec2(uv.x*2.0,uv.y+off_y)).r/(fragCoord.y/384.0)*texture(iChannel1,vec2(uv.x,uv.y+off_y)).r;

    vec3 col = vec3(0.0,0.0,0.0);
    col.r = pow(v,8.0);
    col.g = pow(v,2.0)*0.1;
    col.b = col.g*0.2;

    // output
    fragColor = vec4(clamp(col,0.0,1.0),1.0);
	}
