void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float ramp1 = length(uv - vec2(0.25, 0.5));
    float ramp2 = length(uv - vec2(0.75, 0.5));
    float period = 100.0;

    float wave1 = 0.5 + 0.5 * cos(ramp1 * period - 10.0*iTime);
    float wave2 = 0.5 + 0.5 * cos(ramp2 * period - 10.0*iTime);

    vec3 base = vec3(0, 1, 1);

    // Time varying pixel color
    vec3 col = base * (wave1 + wave2) / 2.0;

    // Output to screen
    fragColor = vec4(col,1.0);
}
