const float PI = 3.14159265;
const float PI_3 = PI / 3.0;

float fn1(float x)
{
    return clamp(sin(x) + 0.5, 0., 1.);
}

float dots(vec2 uv, vec2 center)
{
    vec2 xy = uv - center;
    return sqrt(xy.x * xy.x + xy.y * xy.y);
}

float g(float x)
{
    return (sin(x) + 1.) * 0.5;
}

vec3 hueToCol(float hue)
{
    return vec3(
	    g(hue),
        g(hue + 2. * PI_3),
        g(hue - 2. * PI_3)
    );
}

vec3 mask(float m, vec3 col0, vec3 col1)
{
    return m * col1 + (1.0 - m) * col0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float speed = 24.;
    float hue = PI * fract(iTime * 12.);

    // Normalized pixel coordinates (from 0 to 1)
    float scale = min(iResolution.x, iResolution.y);
    vec2 uv = fragCoord / scale;
	uv -= vec2(iResolution.x / scale, iResolution.y / scale) / 2.;
    uv *= 16.0;
    vec2 gridUV = (uv - vec2(0.5)) * (1.8 - mod(iTime, 0.6) * 1.2);
    vec2 rUV = round(gridUV) + vec2(0.5);

    //float distance = length(uv);
    //float distance = pow(sqrt(uv.x*uv.x+uv.y*uv.y), 1./3.);
    float dist = log(rUV.x*rUV.x+rUV.y*rUV.y);
    float angle = atan(rUV.y, rUV.x);

    vec3 colBak = hueToCol(hue + 2. * PI_3);
    vec3 colSpi = hueToCol(hue);
    vec3 colAdd = hueToCol(hue - 2. * PI_3) - colSpi;

    // Time varying pixel color
    float ring = 0.65 + 0.25 * sin(dist * 2.5 + iTime * 15.0 + PI);

    float dots = clamp((ring * 0.6 - dots(gridUV + vec2(0.5, 0.5), rUV)) * 8.8, 0.0, 1.0);
    float spiral = fn1(dist - angle * 2.0 + iTime * speed * -2.5 + PI);

    // Output to screen
    fragColor = vec4(
        mask(dots, colBak, (spiral * colAdd + colSpi))
      , 1);
}
