#define R iResolution.xy
#define A(U) texture(iChannel0,floor(U)/R)
#define B(U) texture(iChannel1,floor(U)/R)
#define C(U) texture(iChannel2,floor(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage (out vec4 Q, vec2 U)
#define Init if (iFrame < 1) 
#define Border if (U.x<1.||R.x-U.x<1.||U.y<1.||R.y-U.y<1.)
float sg (vec2 p, vec2 a, vec2 b) {
    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	return (length(p-a-(b-a)*i));
}
float mp (vec2 p, vec2 a, vec2 b) {
	vec2 m = 0.5*(a+b); // midpoint
    if (length(a-b)<1e-3) return 1e3; // ignore self
	return dot(p-m,b-m)/dot(b-m,b-m); // pojection
}
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
float tri (vec2 u, vec2 a, vec2 b, vec2 c) {
	float ab = sg(u,a,b);
	float bc = sg(u,b,c);
    float ca = sg(u,c,a);
    return min(ab,min(bc,ca));
}

// Nearest Individual
void X (inout vec4 Q, vec2 U, vec2 r) {
    vec4 n = B(U+r);
    float ln = length(n.xy-U), la = length(Q.xy-U),
         l = abs(mp(U,Q.xy,Q.zw)),
         o = abs(mp(U,Q.xy,n.zw)),
         o1 = abs(mp(U,Q.xy,n.xy));
    if (ln<la) {
    	Q.xy = n.xy;
    }
    if (o<l) {
    	Q.zw = n.zw;
    }
    if (o1<l) {
    	Q.zw = n.xy;
    }
}
void Xr (inout vec4 Q, vec2 U, float r) {
	 X(Q,U,vec2(r,0));
     X(Q,U,vec2(0,r));
     X(Q,U,vec2(0,-r));
     X(Q,U,vec2(-r,0));
}
vec2 grad (vec2 u) {
	vec4
        n = D(u+vec2(0,1)),
        e = D(u+vec2(1,0)),
        s = D(u-vec2(0,1)),
        w = D(u-vec2(1,0));
    return vec2(e.w-w.w,n.w-s.w);
}
Main {
	Q = B(U);
    Xr(Q,U,1.);
    Xr(Q,U,2.);
    Xr(Q,U,3.);
    Xr(Q,U,4.);
    Q.xy += 10.*grad(Q.xy);
    Q.zw += 10.*grad(Q.zw);
    if (iFrame%10==0&&D(U).w>.6) Q.xy = U;
    Init {
    	U = floor(U/100.+0.5)*100.;
        Q = vec4(U,0,0);
    }
}

Main {
    Q = B(U);
	vec2 a = Q.xy, b = Q.zw;
    vec4 col1 = A(a),
         col2 = A(b);
    Q = mix(col1,col2,(0.5+0.5*mp(U,a,b)));
    vec2 u = (vec2(U-a.xy));
    u = normalize(u)*(1.+.1*length(u));
    u = vec2(-u.y,u.x);
    vec4 m = 0.5*(D(U+u)+D(U-u));
    vec4 d = Q-A(U);
    Q = mix(m,Q,.1);
    Q.w = mix((m.w),abs(dot(d,vec4(0.25))),0.9);
}

float map(vec2 p)
{
    return distance(B(p).xy, p);
}

