int  seed = 1;
void srand(int s ) { seed = s; }
int  rand(void) { seed = seed*0x343fd+0x269ec3; return (seed>>16)&32767; }
// --------------------------------------


// --------------------------------------
// hash to initialize the random sequence (copied from Hugo Elias)
// --------------------------------------
int hash( int n )
{
	n = (n << 13) ^ n;
    return n * (n * n * 15731 + 789221) + 1376312589;
}

// --------------------------------------

vec2 randomInCircle( void )
{
    float an = 6.283185*float(rand())/32767.0;
    return vec2(cos(an),sin(an));
}

// WoS
vec3 march( in vec2 p )
{
    float h = 0.0;
	for( int i=0; i<2; i++ )
    {
        h = 1.*map(p);
        if( h<0.) break;
        p = p + h*randomInCircle();
    }
    return C(B(p).xy).xyz/(0.4*h+0.1);
}


Main {
    // init randoms
    vec2 p0 = U;
    ivec2 qqq = ivec2(U);
    srand( hash(qqq.x+hash(qqq.y+hash(iFrame))));

    vec3 col = march(p0);
    vec4 prev = texelFetch(iChannel0, qqq, 0);
    Q = vec4(mix(col,prev.xyz,0.95),1.0);
}
