void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // useful values
    const float pi = 3.14159265;
    const float i_max = pi;
    const float d_i = i_max / 2.;
    vec2 res = iResolution.xy;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy - 0.5*res)/res.y;

    // Time varying pixel color
    vec3 col = .8 - 0.5*cos(iTime+uv.xyx+vec3(sin(iTime)/5.,.2*cos(iTime/4.),1));
    uv = mat2(sin(iTime), cos(iTime), sin(iTime), -cos(iTime)) * uv;

	col.b /= atan(fragCoord.x, fragCoord.y+iTime);
	col.r /= (1./col.b)*(pow(sin(iTime),3.)+pow(cos(iTime),2.))*atan(fragCoord.x, fragCoord.y-cos(iTime));
	col.g /= atan(fragCoord.y, fragCoord.x);

    // Output to screen
    fragColor = vec4(col,1.0);
}
