vec2 cmul (vec2 c1, vec2 c2)
{
	float a = c1.x;
	float b = c1.y;
	float c = c2.x;
	float d = c2.y;

	return vec2 (a * c - b * d, b * c + a * d);
}


//Based on explanation http://www.hiddendimension.com/fractalmath/Divergent_Fractals_Main.html
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = fragCoord / iResolution.xy;
    //scaling real axis to [-2.5, 1.5] and  imaginary axis to  [-1.5, 1.5]
    c = c * vec2(4,3) - vec2(2, 1.5); 

    vec2 z = c;
    fragColor = vec4(0);

    int i = 0;
    for (;i<100;i++)
    {
        if (length(z) >= 2.) 
        {
            break;
        }

       z = cmul(z, z);
       z += sin(iTime);
    }
    fragColor = vec4(i)/100.;
}
