float noise(vec2 p) {
    float a = sin(p.x * 1234.456) * cos(p.y * 5678.1234) * 999.0;
    float b = cos(p.x * 2355.234) * sin(p.y * 3534.3465) * 456.0;
    return fract(a + b);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float scale = 0.5;
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    float time = mod(iTime, 6.35);
    vec2 mouse = (iMouse.xy - 0.5 * iResolution.xy)/iResolution.y;
    vec2 center = vec2(0);
    vec2 follow = mix(center, mouse, clamp(iMouse.w, 0., 1.));

    uv *= scale;
    follow *= scale;
    vec3 col = vec3(0);

    float points = mix(noise(uv), noise(uv + time * 0.000001), 0.01);
    float focus = clamp(length(uv - follow + vec2(sin(time), cos(time)) * 0.01), 0.0, 1.0);
    float waves = pow((1.0 - focus) * sin(pow(focus, 0.5) * 200.0 - iTime) * 0.5 + 0.5, 1.5);
    float mask = smoothstep(0.0, 0.0001, smoothstep(0.2, 0.0, pow(focus + waves * 0.05, 0.8) * points + focus * 0.15));

    col = mix(vec3(0.2, 0.0, 0.1), vec3(0.9, 0.9, 0.8), mask);
    fragColor = vec4(col,1.0);
}
