// https://www.shadertoy.com/view/tdlBz4 is the combinatorial version of this shader

#define NITERS 5

/*
 * PQR is the 1:2:âˆš5 triangle with right angle at the origin,
 * subdivided in 5 tiles as follows:
 *
 * P=(0,1)                                      (2,1)
 * +------------------------------------------------+
 * |\__                                             |
 * |   \____U=(2/5,4/5)                             |
 * |       /\_____                                  |
 * |  1   /  \_   \____                             |
 * |     /     \_      \____                        |
 * |  V /   3    \_         \____T=(6/5,2/5)        |
 * |   /\__        \_    4      /\____              |
 * |  /    \____     \_        /      \_____        |
 * | /   2      \___   \_     /    5        \____   |
 * |/               \____\_  /                   \__|
 * +-----------------------\/-----------------------+
 * Q=(0,0)               S=(1,0)              R=(2,0)
 *
 * V=(1/5,2/5)
 *
 * The lines are defined by the following homogeneous equations:
 *
 * PR:   x + 2*y - 2 == 0
 * QU: 2*x -   y     == 0
 * SV:   x + 2*y - 1 == 0
 * SU: 4*x + 3*y - 4 == 0
 * ST: 2*x -   y - 2 == 0
 *
 * Semispace tests determine to which tile does the input point belong.
 * Each semispace is then scaled, rotated, reflected and translated so
 * that the tile transforms back to the base 1:2:âˆš5 triangle PQR.
 */
float PR(in vec2 p) { return dot(p, vec2(1.,  2.)) - 2.; }
float QU(in vec2 p) { return dot(p, vec2(2., -1.));      }
float ST(in vec2 p) { return dot(p, vec2(2., -1.)) - 2.; }
float SU(in vec2 p) { return dot(p, vec2(4.,  3.)) - 4.; }
float SV(in vec2 p) { return dot(p, vec2(1.,  2.)) - 1.; }

/*
 * Let
 *   R(ğœ—) = mat2(cos(ğœ—), sin(ğœ—), -sin(ğœ—), cos(ğœ—))
 * be the rotation matrix for angle ğœ—, and
 *   Y = mat2(+1, 0, 0, -1)
 * be the reflection matrix across the Y axis. Then:
 *
 * - T0 = T1 * âˆš5*Y*R(atan(+2, -1))  + ( 2,  0)
 * - T0 = T2 * âˆš5*Y*R(atan(+1, +2))  + ( 0,  1)
 * - T0 = T3 * âˆš5*  R(atan(+1, +2))  + ( 0, -1)
 * - T0 = T4 * âˆš5*  R(atan(-1, -2))  + ( 2,  2)
 * - T0 = T5 * âˆš5*Y*R(atan(+1, +2))  + (-2,  2)
 */
vec2 T0(in vec2 p) { return vec2(2., 1.) - p; }
vec2 T1(in vec2 p) { return p*mat2(-1., -2., -2., +1.) + vec2( 2.,  0.); }
vec2 T2(in vec2 p) { return p*mat2(+2., -1., -1., -2.) + vec2( 0.,  1.); }
vec2 T3(in vec2 p) { return p*mat2(+2., -1., +1., +2.) + vec2( 0., -1.); }
vec2 T4(in vec2 p) { return p*mat2(-2., +1., -1., -2.) + vec2( 2.,  2.); }
vec2 T5(in vec2 p) { return p*mat2(+2., -1., -1., -2.) + vec2(-2.,  2.); }

/*
 * Inverting the matrices, we see how each tile results from transforming
 * the base triangle:
 * - T1 == (T0 - ( 2,  0))*R(atan(-2, -1))*Y/âˆš5
 * - T2 == (T0 - ( 0,  1))*R(atan(-1, +2))*Y/âˆš5
 * - T3 == (T0 - ( 0, -1))*R(atan(-1, +2))  /âˆš5
 * - T4 == (T0 - ( 2,  2))*R(atan(+1, -2))  /âˆš5
 * - T5 == (T0 - (-2,  2))*R(atan(-1, +2))*Y/âˆš5
 *
 * Modulo Ï€/2 and discounting reflections, each tile gets rotated with
 * respect to the parent triangle by one of two possible angles:
 * - T1, T2, T5, by atan(0.5)
 * - T3, T4,     by atan(2)
 * and clearly both angles are complementary.
 */
const float A = 0.2951672353; // atan(0.5) / (0.5*PI)

// See http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl
vec3 hsv2rgb(in vec3 hsv)
{
    vec3 hue = clamp(abs(6.*fract(hsv.xxx + vec3(0.,2./3.,1./3.)) - 3.) - 1., 0., 1.);
    return hsv.z * mix(vec3(1.), hue, hsv.y);
}

// PRE 0 <= p.x < 2 && 0 <= p.y < 1
vec3 pinwheel(in vec2 p, in int n)
{
    float a = 0.; // Accumulated rotation angle as a fraction of Ï€/2
    float h = 0.; // Hash, or sequence of triangles in the subdivision

    // Reflect upper right triangle into the lower left one
    if (PR(p) > 0.) {
		p = T0(p);
        h = 0.5;
    }

    // Find tile and inverse transform the point
    for (int i = 0; i < n; i++) {
        // INV 0 <= p.x < 2 && 0 <= p.y < 1 && 0 <= p.x + 2*p.y <= 2
        if (QU(p) < 0.) {
            // Triangle 1
            p  = T1(p);
            a += A;
            h += 0.;
        } else if (SV(p) < 0.) {
            // Triangle 2
            p  = T2(p);
            a += A;
            h += 1.;
        } else if (SU(p) < 0.) {
            // Triangle 3
            p  = T3(p);
            a -= A;
            h += 2.;
        } else if (ST(p) < 0.) {
            // Triangle 4
            p  = T4(p);
            a -= A;
            h += 3.;
        } else {
            // Triangle 5
            p  = T5(p);
            a += A;
            h += 4.;
        }
	    a  = fract(a);
        h *= 0.2;
    }
	// Barycentric coordinates of the point
    vec3 b = vec3(p.y, 0.5*p.x, 1. - 0.5*p.x - p.y);
    return b;//hsv2rgb(vec3(a, 1., 1.));
}

float triangle(in float x)
{
    return 1. - 2.*abs(fract(x) - 0.5);
}

#define T (iTime / 6.0)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.x;
    // Point in the [0,2]*[0,1] domain
    vec2 p = vec2(2.0*fract(uv.x), fract(2.0*uv.y));
    int n = int(round(float(NITERS) * triangle(T)));
    vec3 col = pinwheel(p, n);
    fragColor = vec4(col, 1.);
}
