#define CASE 0  // 0: opt for dominant opinion 1: only if N more opponants

#define N(U)   fract(sin(dot(U, vec2(12.9898, 78.233)) - iTime) * 43758.5453)

void mainImage( out vec4 O, vec2 U )
{
    O -= O;
  //for (int i=1; i<8; i++) if (i!=2 && i!=6) // 4-neighbors
    for (int i=0; i<9; i++)                   // 8 neighbors
        O += T(U+vec2(i%3-1,i/3-1));
    O -= T(U);

#if CASE==0
    O = sign( O ); // opt for dominant neighbors opinion
 // O =  O /7.9;   // variant with opinion strength
#else              // change mind only if N more people think the opposite
    if ( abs( O - T(U) ).x > 3. ) O = sign( O );
#endif

    iFrame==0 // || N(U+.1)>.99     // uncomment ||... for injecting noise
        ? O = vec4( N(U) < .5 ? -1. : 1. ) : O;
                        // ^^^ try other initial balance
}

//#define T(U) texelFetch( iChannel0, ivec2(U), 0)
//#define T(U) texelFetch( iChannel0, ivec2(U)%ivec2(iResolution.xy), 0)
#define T(U) texelFetch( iChannel0, ivec2(mod(U,iResolution.xy)), 0)
