#define SCALE 800.0
#define SQUEEZE_RATE 4.0
#define SPEED -100.0

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 pixel = (fragCoord -.5 * iResolution.xy) / iResolution.y;
    float squizeFactor = sin(iTime / SQUEEZE_RATE) * 2.0 + 1.0;
    pixel = pow(abs(pixel), vec2(squizeFactor));
    pixel *= vec2(SCALE);
    float x = pixel.x;
    float y = pixel.y;
 	float t = iTime * SPEED;    
    fragColor = vec4(
          mod(x + mod(y + t, 64.0) + y + mod(x + t, 64.0), 256.0) / 256.0,
          0,
          mod(x + mod(y + t, 128.0) + y + mod(x + t, 128.0), 256.0) / 256.0,
        1.0
    );
}
