#define r(x) mat2(cos(x),-sin(x),sin(x),cos(x))

float b(vec2 p, vec2 b) {
  vec2 d = abs(p) - b;
  return length(max(d,0.)) + min(max(d.x,d.y),0.); 
}

#define mainImage(O, u)					\
    vec2 R = iResolution.xy, m = iMouse.xy/R;				\
    float z = exp(-10. * m.y + 1.), d = 1., t = iTime*.5;	\
    vec3 p = vec3(r(6.28 * m.x) * (u+u-R)/R.y * z - .5,1);	\
        \
    for(int i = -1, N = int(t)%5+1; ++i<N;\
        d = min(d, b( vec2(p = vec3(mod(p.xy-1.,2.)-1.,p.z)*3.), vec2(1) )/p.z )\
       );\
    O = vec4(smoothstep(1.5,0., int(t*2.)%2 == 0 ? -d/z*R.y : abs(d)/z*R.y-.5));
