float circle(vec2 pixel, vec2 center, float radius, float blur) {
    float d = distance(pixel, center);
    float mask = smoothstep(radius, radius - (blur + 0.0001), d);
    return mask;
}

float band(float t, float start, float end, float blur) {
    float step1 = smoothstep(start - blur, start + blur, t);
    float step2 = smoothstep(end + blur, end - blur, t);
    return step1 * step2;
}

float rect(vec2 pixel, float left, float right, float bottom, float top, float blur) {
    float band1 = band(pixel.x, left, right, blur);
    float band2 = band(pixel.y, bottom, top, blur);
    return band1 * band2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )    
{
    vec2 pixel = fragCoord / iResolution.xy;
    vec2 mouse = iMouse.xy / iResolution.xy;
    float ratio = iResolution.x / iResolution.y;

    pixel -= 0.5;
    mouse -= 0.5;
    pixel.x *= ratio;
    mouse.x *= ratio;

    vec3 texture = texture(iChannel0, pixel).xyz;
    float mask = rect(pixel, -0.3, 0.2, -0.3, 0.3, 0.1);
    mask += circle(pixel, mouse, 0.4, 0.3);

    vec3 color = clamp(mix(vec3(0), texture, mask), vec3(0.0), texture);

    fragColor = vec4(vec3(color), 1.0);
}
