float shape(vec2 uv, float posX,float posY,float sizeNum,float handOne,float handTwo)
{
    float ratio =iResolution.x/iResolution.y;
    uv.x *= ratio;
    vec2 shapePos= vec2(posX*ratio,posY);
    float dist = distance(shapePos,uv)*sizeNum;
    uv -=shapePos;
    float angle= atan(uv.y,uv.x);
    float radius = angle;
    radius = abs(cos(angle*handOne)*sin(angle*handTwo+iTime));
    float value = 1.-step(radius,dist);

    return value;
}
float circle(vec2 uv,float distanceAa, float distanceAb,float ditanceBa,float distanceBb,float size)
{

    float val = distance(uv, vec2(distanceAa, distanceAb)) * distance(uv, vec2(ditanceBa, distanceBb));

	val = step(val, size);

    return val;
}

float quad(vec2 uv,float h, float w,float posX, float posY)
{
    float quad = step(posX-h ,uv.x)-step(posX+h,uv.x);
	quad = quad + step(posY-w,uv.y)-step(0.,uv.y)-step(posY+w,uv.y);
    return quad;
}

